constexpr u64 get_all_versions_index(revision_checker::file_header_info fhi) {
    for (u64 i = 0; i < sizeof(Land::AllVersions) / sizeof(Land::AllVersions[0]); ++i) {
        if (Land::AllVersions[i].revision_info.major == fhi.major &&
            Land::AllVersions[i].revision_info.minor == fhi.minor) {
            return i;
        }
    }
    return 0x0;
}
